using System;
using System.Collections.Generic;
using System.Drawing;

namespace AdventOfCode2017
{
    public class Day19
    {
        private string _letters;
        private string[] _lines;
        private readonly List<char> _pathChars = new List<char> { '|', '-', '+' };

        public string Part1(string input)
        {
            _lines = input.Split(new[] { '\r', '\n' }, StringSplitOptions.RemoveEmptyEntries);
            var curPos = new Point(_lines[0].IndexOf('|'), 0);
            var curDir = Direction.DOWN;
            var pathing = true;
            _letters = "";
            while (pathing)
            {
                switch (curDir)
                {
                    case Direction.DOWN:
                        if (HasPath(curPos.X, curPos.Y + 1))
                        {
                            curPos.Y++;
                        }
                        else if (HasPath(curPos.X - 1, curPos.Y))
                        {
                            curDir = Direction.LEFT;
                            curPos.X--;
                        }
                        else if (HasPath(curPos.X + 1, curPos.Y))
                        {
                            curDir = Direction.RIGHT;
                            curPos.X++;
                        }
                        else
                            pathing = false;
                        break;
                    case Direction.LEFT:
                        if (HasPath(curPos.X - 1, curPos.Y))
                        {
                            curPos.X--;
                        }
                        else if (HasPath(curPos.X, curPos.Y + 1))
                        {
                            curDir = Direction.DOWN;
                            curPos.Y++;
                        }
                        else if (HasPath(curPos.X, curPos.Y - 1))
                        {
                            curDir = Direction.UP;
                            curPos.Y--;
                        }
                        else
                            pathing = false;
                        break;
                    case Direction.UP:
                        if (HasPath(curPos.X, curPos.Y - 1))
                        {
                            curPos.Y--;
                        }
                        else if (HasPath(curPos.X - 1, curPos.Y))
                        {
                            curDir = Direction.LEFT;
                            curPos.X--;
                        }
                        else if (HasPath(curPos.X + 1, curPos.Y))
                        {
                            curDir = Direction.RIGHT;
                            curPos.X++;
                        }
                        else
                            pathing = false;
                        break;
                    case Direction.RIGHT:
                        if (HasPath(curPos.X + 1, curPos.Y))
                        {
                            curPos.X++;
                        }
                        else if (HasPath(curPos.X, curPos.Y + 1))
                        {
                            curDir = Direction.DOWN;
                            curPos.Y++;
                        }
                        else if (HasPath(curPos.X, curPos.Y - 1))
                        {
                            curDir = Direction.UP;
                            curPos.Y--;
                        }
                        else
                            pathing = false;
                        break;
                }
            }
            return _letters;
        }

        public int Part2(string input)
        {
            _lines = input.Split(new[] { '\r', '\n' }, StringSplitOptions.RemoveEmptyEntries);
            var curPos = new Point(_lines[0].IndexOf('|'), 0);
            var curDir = Direction.DOWN;
            var pathing = true;
            var steps = 1;
            _letters = "";
            while (pathing)
            {
                switch (curDir)
                {
                    case Direction.DOWN:
                        if (HasPath(curPos.X, curPos.Y + 1))
                        {
                            curPos.Y++;
                        }
                        else if (HasPath(curPos.X - 1, curPos.Y))
                        {
                            curDir = Direction.LEFT;
                            curPos.X--;
                        }
                        else if (HasPath(curPos.X + 1, curPos.Y))
                        {
                            curDir = Direction.RIGHT;
                            curPos.X++;
                        }
                        else
                            pathing = false;
                        break;
                    case Direction.LEFT:
                        if (HasPath(curPos.X - 1, curPos.Y))
                        {
                            curPos.X--;
                        }
                        else if (HasPath(curPos.X, curPos.Y + 1))
                        {
                            curDir = Direction.DOWN;
                            curPos.Y++;
                        }
                        else if (HasPath(curPos.X, curPos.Y - 1))
                        {
                            curDir = Direction.UP;
                            curPos.Y--;
                        }
                        else
                            pathing = false;
                        break;
                    case Direction.UP:
                        if (HasPath(curPos.X, curPos.Y - 1))
                        {
                            curPos.Y--;
                        }
                        else if (HasPath(curPos.X - 1, curPos.Y))
                        {
                            curDir = Direction.LEFT;
                            curPos.X--;
                        }
                        else if (HasPath(curPos.X + 1, curPos.Y))
                        {
                            curDir = Direction.RIGHT;
                            curPos.X++;
                        }
                        else
                            pathing = false;
                        break;
                    case Direction.RIGHT:
                        if (HasPath(curPos.X + 1, curPos.Y))
                        {
                            curPos.X++;
                        }
                        else if (HasPath(curPos.X, curPos.Y + 1))
                        {
                            curDir = Direction.DOWN;
                            curPos.Y++;
                        }
                        else if (HasPath(curPos.X, curPos.Y - 1))
                        {
                            curDir = Direction.UP;
                            curPos.Y--;
                        }
                        else
                            pathing = false;
                        break;
                }
                if (pathing)
                    steps++;
            }
            return steps;
        }

        private bool HasPath(int x, int y)
        {
            if (y < 0 || y >= _lines.Length)
                return false;
            if (x < 0 || x >= _lines[y].Length)
                return false;
            var character = _lines[y][x];
            if (character == ' ')
                return false;
            if (!_pathChars.Contains(character))
                _letters += character;
            return true;
        }

        public static void Run()
        {
            var day19 = new Day19();
            Console.WriteLine("\n###############\n###############\nDay 19\n###############\n###############\n");
            Console.WriteLine("\n###############\nPart 1\n###############\n");
            Console.WriteLine(day19.Part1(@"
     |          
     |  +--+    
     A  |  C    
 F---|----E|--+ 
     |  |  |  D 
     +B-+  +--+ 
"));
            Console.WriteLine(day19.Part1(Input));

            Console.WriteLine("\n###############\nPart 2\n###############\n");
            Console.WriteLine(day19.Part2(@"
     |          
     |  +--+    
     A  |  C    
 F---|----E|--+ 
     |  |  |  D 
     +B-+  +--+ 
"));
            Console.WriteLine(day19.Part2(Input));
        }

